  gameScreen.updateDots = function (a, b, connect, remove) {
    var dots;
    var scale = 0.75;
    var alpha = 0.5;

    if (!connect && !remove) {
      scale = 0.5;
      alpha = 0.1;
    }

    if (remove) {
      scale = 0;
      alpha = 0;
    }

    var id = a.i - b.i;
    var jd = a.j - b.j;
    var dir = id + jd;

    if (id == 0 && jd == -1) {
      dots = a.rightDots;

      if (remove) {
        a.rightDots = null;
        b.leftDots = null;
      }
    }

    if (id == 0 && jd == 1) {
      dots = a.leftDots;

      if (remove) {
        b.rightDots = null;
        a.leftDots = null;
      }
    }

    if (id == -1 && jd == 0) {
      dots = a.downDots;

      if (remove) {
        a.downDots = null;
        b.upDots = null;
      }
    }

    if (id == 1 && jd == 0) {
      dots = a.upDots;

      if (remove) {
        b.downDots = null;
        a.upDots = null;
      }
    }

    if (dots == undefined) {
      return;
    }

    var len = dots.length;
    var delay;

    for (var i = 0; i < len; i++) {
      if (!dots[i]) return;
      delay = distance(dots[i], a) * 0.0002;
      dots[i].tween = TweenMax.to(dots[i], 0.12, {
        pixi: {
          scale: scale,
          alpha: alpha
        },
        delay: delay,
        ease: Power2.easeIn,
        onComplete: function onComplete(d, r) {
          if (r) {
            d["delete"]();
          }
        },
        onCompleteParams: [dots[i], remove]
      });
    }
  };

